import React from "react";
import { Link } from "react-router-dom";

const Header = ({ isAuthenticated, onLogout, showLoginButton }) => {
  return (
    <header className="bg-gray-800 text-white p-4 shadow-md">
      <div className="flex justify-center items-center space-x-4"> {/* Centering the logo and text */}
        {/* Logo */}
        <Link to="/" className="flex items-center space-x-2">
          <img src="../assets/logo.png" alt="Logo" className="w-8 h-8" />
          <h1 className="text-xl font-bold">Project Manager</h1>
        </Link>
      </div>

      {/* Navigation */}
      <nav className="flex justify-center mt-4">
        <ul className="flex space-x-6">
          <li>
            <Link to="/" className="hover:text-gray-400">Home</Link>
          </li>
          <li>
            <Link to="/contact-us" className="hover:text-gray-400">Contact Us</Link>
          </li>
          {showLoginButton && !isAuthenticated && (
            <li>
              <Link to="/" className="hover:text-gray-400">Login</Link>
            </li>
          )}
          {isAuthenticated && (
            <li>
              <button onClick={onLogout} className="hover:text-gray-400">Logout</button>
            </li>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
