import React, { useState } from "react";
import { Routes, Route, Link } from "react-router-dom";
import ProjectDetail from "./components/ProjectDetail";
import Header from "./components/Header";
import { Toaster } from "react-hot-toast";

function App() {
  // State to manage projects
  const [projects, setProjects] = useState([
    { id: 1, name: "Project 1", description: "This is Project 1 description" },
    { id: 2, name: "Project 2", description: "This is Project 2 description" },
    { id: 3, name: "Project 3", description: "This is Project 3 description" },
  ]);

  // Add a new project
  const addProject = (newProject) => {
    setProjects([...projects, newProject]);
  };

  return (
    <div>
      <Header />
      <Toaster position="top-right" gutter={8} />
      <Routes>
        <Route
          path="/"
          element={
            <div className="flex flex-col items-center w-full pt-10">
              <h1 className="text-xl text-gray-600">Select a Project</h1>
              <div className="space-y-4">
                {/* List of Projects */}
                {projects.map((project) => (
                  <Link
                    to={`/project/${project.id}`}
                    key={project.id}
                    className="block text-blue-500 hover:underline"
                  >
                    {project.name}
                  </Link>
                ))}
                <button
                  onClick={() =>
                    addProject({
                      id: projects.length + 1,
                      name: `Project ${projects.length + 1}`,
                      description: `This is Project ${projects.length + 1} description`,
                    })
                  }
                  className="mt-4 bg-blue-500 text-white px-4 py-2 rounded"
                >
                  Add New Project
                </button>
              </div>
            </div>
          }
        />
        {/* Route to show project details */}
        <Route path="/project/:projectId" element={<ProjectDetail projects={projects} />} />
      </Routes>
    </div>
  );
}

export default App;
