import React from "react";
import logo from "../assets/logo.svg";  // Ensure your logo is in the correct path

const Header = () => {
  return (
    <div className="bg-gray-800 text-white p-4 flex items-center justify-center relative">
      {/* Centered Title */}
      <div className="absolute left-0 right-0 text-center">
        <h1 className="text-xl font-semibold">Project Management App</h1>
      </div>
      
      {/* Logo on the right */}
      <div className="absolute right-4 top-4">
        <img src={logo} alt="App Logo" className="h-8 w-8" />
      </div>
    </div>
  );
};

export default Header;
