import React, { useState } from "react";
import { Routes, Route } from "react-router-dom";
import { Toaster } from "react-hot-toast";
import Header from "./components/Header";
import Sidebar from "./components/Sidebar";
import Footer from "./components/Footer";
import Login from "./components/Login";
import ProjectDetail from "./components/ProjectDetail"; // New component for showing project details
import ContactUs from "./components/ContactUs";
import Task from "./components/Task";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [projects, setProjects] = useState([
    { id: 1, name: "Project 1", description: "This is Project 1" },
    { id: 2, name: "Project 2", description: "This is Project 2" },
    { id: 3, name: "Project 3", description: "This is Project 3" },
  ]);

  const [userCredentials, setUserCredentials] = useState({ email: "", password: "" });

  const handleLogin = (email, password) => {
    // Simulating a login process
    setUserCredentials({ email, password });
    setIsAuthenticated(true);
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUserCredentials({ email: "", password: "" });
  };

  // Function to add a new project
  const addProject = (newProject) => {
    setProjects((prevProjects) => [...prevProjects, newProject]);
  };

  // Function to delete a project
  const deleteProject = (projectId) => {
    setProjects((prevProjects) => prevProjects.filter((project) => project.id !== projectId));
  };

  return (
    <div className="flex flex-col min-h-screen">
      {/* Header Component */}
      <Header isAuthenticated={isAuthenticated} onLogout={handleLogout} />

      {/* Main Content */}
      <div className="flex flex-1">
        <Sidebar
          projects={projects}
          onAddProject={addProject}
          onDeleteProject={deleteProject} // Passing delete function to sidebar
        />

        <div className="flex-1 p-6">
          <Toaster position="top-right" gutter={8} />

          <Routes>
            {/* Route for Project Details */}
            <Route
              path="/project/:projectId"
              element={
                isAuthenticated ? (
                  <ProjectDetail projects={projects} />
                ) : (
                  <Login onLogin={handleLogin} />
                )
              }
            />
            {/* Home or Task Page */}
            <Route
              path="/"
              element={isAuthenticated ? <Task /> : <Login onLogin={handleLogin} />}
            />
            {/* Contact Us Route */}
            <Route path="/contact-us" element={<ContactUs />} />
          </Routes>
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
}

export default App;
