import React, { useState } from "react";
import { Link } from "react-router-dom";
import logo from "../assets/projectlogo.jpg"; // Assuming logo.jpg is in assets folder

const Header = () => {
  // State for login/logout
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [showLogin, setShowLogin] = useState(false);

  // Toggle login/logout
  const handleLoginLogout = () => {
    if (isAuthenticated) {
      // Simulate logout
      setIsAuthenticated(false);
    } else {
      // Simulate login
      setIsAuthenticated(true);
    }
  };

  // Toggle Login button visibility
  const toggleLoginVisibility = () => {
    setShowLogin(!showLogin);
  };

  return (
    <header className="bg-gray-800 text-white p-4 shadow-md w-full">
      <div className="flex items-center justify-between mx-auto max-w-screen-xl">
        {/* Logo and description */}
        <Link to="/" className="flex items-center space-x-2">
          <img src={logo} alt="Logo" className="w-12 h-12" />
          <div>
            <h1 className="text-2xl font-bold">Project Manager</h1>
            <p className="text-sm text-gray-400">Manage your projects with ease and efficiency</p>
          </div>
        </Link>

        {/* Navigation */}
        <nav>
          <ul className="flex space-x-6">
            <li>
              <Link to="/" className="hover:text-gray-400">Home</Link>
            </li>
            <li>
              <Link to="/contact-us" className="hover:text-gray-400">Contact Us</Link>
            </li>

            {/* Conditionally render login or logout button */}
            {isAuthenticated ? (
              <li>
                <button onClick={handleLoginLogout} className="hover:text-gray-400">
                  Logout
                </button>
              </li>
            ) : (
              <li>
                <button onClick={handleLoginLogout} className="hover:text-gray-400">
                  Login
                </button>
              </li>
            )}

            {/* Show the login button only when the user clicks the "Login" option */}
            {showLogin && !isAuthenticated && (
              <li>
                <button onClick={handleLoginLogout} className="hover:text-gray-400">
                  Log in to Start
                </button>
              </li>
            )}
          </ul>
        </nav>

        {/* Toggle button to show login form (if applicable) */}
        <button
          onClick={toggleLoginVisibility}
          className="bg-blue-600 text-white py-1 px-4 rounded-lg hover:bg-blue-500"
        >
          Toggle Login
        </button>
      </div>
    </header>
  );
};

export default Header;
